def compare_hash_values(given_hash_value):
	with open('hash_file.txt', 'r') as file:
		for line in file:
			hash_value = line.strip()
			if hash_value is str(given_hash_value):
				print(True)
				break
			else:
				print(given_hash_value, hash_value)

